import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Slider from "react-slick";
import { Box, Typography, Card, CardContent, useTheme, useMediaQuery } from '@mui/material';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import '../style/css/performance.css';

const Performance = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 3000,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 2,
        }
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
        }
      }
    ]
  };

  const cards = [
    {
      title: "REVENUE OPTIMIZATION",
      desc: "Dynamic pricing strategies tailored to market trends for maximum earnings",
    },
    {
      title: "MARKETING & MANAGEMENT",
      desc: "Your property promoted across major platforms to maximize visibility",
    },
    {
      title: "BOOSTED LISTING VISIBILITY",
      desc: "By maximizing key metrics, our strategy boosts your properties’ ranking in searches",
    },
  ];

  return (
 <Box className="card-grid" sx={{ px: { xs: 2, sm: 3, md: 4 } }}>
  <Box 
    sx={{
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'center',
      gap: { xs: 3, sm: 4, md: 5 },
    }}
  >
    {cards.map((card, index) => (
      <Card
        key={index}
        sx={{
          flex: '1 1 300px',
          maxWidth: 360,
          minHeight: { xs: '350px', sm: '400px', md: '450px' },
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          borderRadius: '50px',
          boxShadow: 4,
          transition: 'transform 0.3s, box-shadow 0.3s',
          background: (index === 1)
            ? 'radial-gradient(circle at 80% center, #a0cbe8 0%, #d4eaf6 40%, #ffffff 80%)'
            : 'white',
          '&:hover': {
            transform: 'translateY(-5px)',
            boxShadow: 6
          }
        }}
      >
        <CardContent sx={{ p: { xs: 3, sm: 4 }, textAlign: 'left' }}>
          <Typography 
            variant="h6" 
            sx={{ 
              fontWeight: 'bold',
              mb: 2,
              color: '#323232',
              fontSize: { 
                xs: '22px', 
                sm: '26px', 
                md: '30px', 
                lg: '40px' 
              },
              fontFamily: '"Raleway-Bold", sans-serif',
              textTransform: 'uppercase',
              lineHeight: 1.2
            }}
          >
            {card.title}
          </Typography>
          <Typography 
            variant="body1" 
            sx={{ 
              color: '#323232',
              fontSize: { 
                xs: '16px', 
                sm: '18px', 
                md: '20px', 
                lg: '24px' 
              },
              fontFamily: '"Raleway-Regular", sans-serif',
              lineHeight: 1.4
            }}
          >
            {card.desc}
          </Typography>
        </CardContent>
      </Card>
    ))}
  </Box>
</Box>

  );
};

export default Performance;
